SELECT f.*,fold.fol_id,e.emp_name,e.emp_id FROM folder f LEFT OUTER JOIN employee e ON f.fol_emp_id=e.emp_id LEFT OUTER JOIN folder fold ON f.fol_super_fol=fold.fol_id
and f.fol_emp_id=#{empId,jdbcType=INTEGER}
and f.fol_super_fol=#{folId,jdbcType=INTEGER}
and instr(f.fol_name,#{folName,jdbcType=VARCHAR})!=0
LIMIT #{start},#{pageSize}

