mapper.select:
select count(*)
from employee


	
		and dept_id = #{deptId,jdbcType=INTEGER}
	
	
		and job_id = #{jobId,jdbcType=INTEGER}
	
	
	    and emp_sex = #{empSex}
	
	
		
		and emp_name like concat('%',#{empName,jdbcType=VARCHAR},'%')

SELECT * FROM employee e LEFT OUTER JOIN department d ON e.`emp_dept_id` = d.`dept_id` LEFT OUTER JOIN job j ON e.`emp_job_id` = j.`job_id`
and instr(emp_name,#{empName,jdbcType=VARCHAR})!=0
and instr(emp_sex,#{empSex,jdbcType=VARCHAR})!=0
and e.dept_id = #{deptId,jdbcType=INTEGER}
and e.job_id = #{jobId,jdbcType=INTEGER}
LIMIT #{start}, #{pageSize}
